pragma solidity ^0.4.23;

contract zephyrUtils {

    function areStringsEqual(string _a, string _b) internal pure returns (bool) {
        bytes memory a = bytes(_a);
        bytes memory b = bytes(_b);
        uint minLength = a.length;

        if (b.length < minLength){
            minLength = b.length;
        } 

        for (uint i = 0; i < minLength; i ++){
            if (a[i] < b[i])
                return false;
            else if (a[i] > b[i]){
                return false;
            }
        }

        if (a.length < b.length)
            return false;
        else if (a.length > b.length)
            return false;
        else
            return true;
    }
}
